function ListNode(val) {
    this.val = val;
    this.next = null;
}

function fromArray(array) {
    if(array.length === 0) {
        return null;
    }
    var node = new ListNode(array[0]);
    node.next = fromArray(array.slice(1));
    return node;
}

function toArray(head) {
    var ret = new Array();
    while(head !== null) {
        ret.push(head.val);
        head = head.next;
    }
    return ret;
}

exports.ListNode  = ListNode;
exports.fromArray = fromArray;
exports.toArray   = toArray;

var test = function() {
    let list = new ListNode(1);
    console.assert(list.val === 1);
    let test = [1, 2, 3, 4];
    console.assert(test.toString() === toArray(fromArray(test)).toString());
};
